import CoursePostingForm from './Page/Course/Courses';

function App() {

  return (
    <ChakraProvider theme={theme}>
      <Router>
         <div>
        <Nav />
        <Switch>
        <Route path="/" exact component={Homepage } />
        <Route path="/home/:id" component={Homedetail } />

        <Route path="/Favourite" exact component={Favourite} />

        </Switch>
    </div> 
      </Router>
    </ChakraProvider>
  );
}

export default App;
