import React, { useState } from 'react';
import { FiShoppingCart } from "react-icons/fi";

import {
  Box,
  Heading,
  FormControl,
  FormLabel,
  Input,
  Textarea,
  Button,
  Select,
  Divider,
  NumberInput,
  NumberInputField,
  NumberInputStepper,
  NumberIncrementStepper,
  NumberDecrementStepper,
  Link,
  Radio,
  RadioGroup,
  Stack,
  Flex,
  Center,
  HStack,
  IconButton,
  Badge,
} from '@chakra-ui/react';

const CoursePostingForm = () => {
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [image, setImage] = useState(null);
  const [promotionalImage, setPromotionalImage] = useState(null);
  const [date, setDate] = useState('');
  const [category1, setCategory1] = useState('');
  const [category2, setCategory2] = useState('');
  const [category3, setCategory3] = useState('');
  const [language, setLanguage] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [level, setLevel] = useState('beginner');

  const handleSubmit = event => {
    event.preventDefault();
    // handle form submission here
  };

  function handleImageUpload(event) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        setImage(reader.result);
      };
    }
  }

  function handlePromotionalImageUpload(event) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        setPromotionalImage(reader.result);
      };
    }
  }

  return (
<Box>
   <Box bg={useColorModeValue('gray.100', 'gray.100')} >
      <Flex
      spacing={6}
        bg={useColorModeValue('gray.50', 'gray.900')}
        color={useColorModeValue('#000000', '#000')}
        minH={'70px'}
        py={{ base: 4 }}
        px={{ base: 4 }}
        borderBottom={1}
        borderStyle={'normal'}
        borderColor={useColorModeValue('gray.200', 'gray.900')}
        align={'center'}>
        <Flex
          flex={{ base: 1, md: 'auto' }}
          ml={{ base: -2 }}
          display={{ base: 'flex', md: 'none' }}>
          <IconButton
            onClick={onToggle}
            icon={
              isOpen ? <CloseIcon w={4} h={4} /> : <HamburgerIcon w={6} h={6} />
            }
            variant={'ghost'}
            aria-label={'Toggle Navigation'}
          />
        </Flex>
        <Flex flex={{ base: 1 }} justify={{ base: 'space-evenly', md: 'space-around' }}>
        <Box display="flex" alignItems="center">
          <Image src={lms_logo} alt="Logo" boxSize="40px" objectFit="contain" />
        </Box>

          <Box display={{ base: 'none', md: 'block' }}>
            <InputGroup>
              <InputRightElement
                cursor="pointer"
                bg={'#197DDA'}
                pointerEvents="cursor"
                children={<SearchIcon color="gray.50" />}
              />
              <Input type="text" placeholder="Find A Course" />
            </InputGroup>
            </Box>

          <Flex display={{ base: 'none', md: 'flex' }} ml={2}>
            <DesktopNav />
          </Flex>
        </Flex>

        <Stack
          flex={{ base: 1, md: 0 }}
          justify={'flex-end'}
          direction={'row'}
          spacing={6}>
          {/* <Button onClick={toggleColorMode}>
              {colorMode === 'light' ? <MoonIcon /> : <SunIcon />}
          </Button> */}
          <Button
            display={{ base: 'none', md: 'inline-flex' }}
            fontSize={'md'}
            fontWeight={500}
            color={'white'}
            bg={useColorModeValue('#197DDA', '#FAD744')}
            href={'#contact'}
            _hover={{
              bg: 'blue.400',
            }}>
            sign up
          </Button>
          <Button
            as={'a'}
            color={'#197DDA'}
            fontSize={'md'}
            fontWeight={500}
            variant={'outline'}
            href={'#'}>
            Login
          </Button>
        </Stack>
      </Flex>

      <Collapse in={isOpen} animateOpacity>
        <MobileNav />
      </Collapse>
    </Box>

      <Box flex="1" bg="red.100" p="4" alignItems="center">
        <Link href="/" fontSize="xl" fontWeight="bold">
          Course Website
        </Link>
        <Box>
          <Flex></Flex>
        <Link href="/courses" mr="4">
          Courses
        </Link>
        <Link href="/about" mr="4">
          About
        </Link>
        <Link href="/contact" mr="4">
          Contact
        </Link>
        <Link href="/cart">
          <IconButton
            icon={<FiShoppingCart />}
            aria-label="Cart"
            variant="ghost"
            size="lg"
          />
          <Badge ml="2" colorScheme="red">
            0
          </Badge>
        </Link>
      </Box>
      </Box>


<Flex
      width={{ base: '100%', md: '90%' }}
      height={{ base: '50%', md: '200%' }}
      maxWidth="1200px"
      margin="auto"
      sx={{
        '@media (max-width: 768px)': {
          height: '75vh',
        },
        '@media (max-width: 480px)': {
          width: '90%',
        },
      }}
    >
      <Box w="30%" mt="100px" p="10px" mr="80px">
        <RadioGroup defaultValue="Intended Learners">
          <Stack spacing={3}>
            <Radio value="Intended Learners" color={'#197DDA'}>
              Intended Learners
            </Radio>
            <Radio value="Course Content" color={'#197DDA'}>
              Course Content
            </Radio>
            <Radio value="Course Landing Page" color={'#197DDA'}>
              Course Landing Page
            </Radio>
            <Radio value="Pricing" color={'#197DDA'}>
              Pricing
            </Radio>

            <Button
              colorScheme="blue"
              size="md"
              sx={{ borderRadius: '8px', fontWeight: 'bold' }}
            >
              Publish Course
            </Button>
          </Stack>
        </RadioGroup>
      </Box>

      <Divider
        h="300px"
        mt="50px"
        borderWidth="1px"
        color={'#197DDA'}
        orientation="vertical"
      />
      <Box p="6">
        <Center>
          <Heading as="h1" size="xl" mb="6">
            Title of Course
          </Heading>
        </Center>
        <Divider borderWidth="1px" marginBottom="19px" />

        <p>
          Your course landing page is the page first seen by a user or an
          intending learner when the click your course. Make sure it is
          compelling enough to drive learners to enroll in your course.
        </p>
        <form onSubmit={handleSubmit}>
          <FormControl id="title" mb="4">
            <FormLabel>Course Title</FormLabel>
            <Input
              type="text"
              placeholder="Introduction to web Design"
              value={title}
              onChange={event => setTitle(event.target.value)}
            />
          </FormControl>

          <FormControl id="author" mb="4">
            <FormLabel>Course Description</FormLabel>
            <Input
              type="text"
              placeholder="Enter the course author"
              value={author}
              onChange={event => setAuthor(event.target.value)}
            />
          </FormControl>
          <FormControl></FormControl>
          {/* <FormControl id="image" mb="4">
            <FormLabel>Image URL</FormLabel>
            <Input
              type="text"
              placeholder="Enter the course image URL"
              value={image}
              onChange={event => setImage(event.target.value)}
            />
          </FormControl> */}

          {/* <FormControl id="date" mb="4">
            <FormLabel>Date</FormLabel>
            <Input
              type="date"
              value={date}
              onChange={event => setDate(event.target.value)}
            />
          </FormControl> */}

          <FormControl id="basicInfo" mb="4">
            <Flex justifyContent="space-between" width="100%">
              <Select
                placeholder="Select level"
                value={category1}
                onChange={event => setCategory1(event.target.value)}
                marginRight="2"
              >
                <option value="programming">Programming</option>
                <option value="design">Design</option>
                <option value="business">Business</option>
                <option value="marketing">Marketing</option>
              </Select>
              <Select
                placeholder="Select Category"
                value={category2}
                onChange={event => setCategory2(event.target.value)}
                marginRight="2"
              >
                <option value="programming">Programming</option>
                <option value="design">Design</option>
                <option value="business">Business</option>
                <option value="marketing">Marketing</option>
              </Select>
              <Select
                placeholder="Select Sub-category"
                value={category3}
                onChange={event => setCategory3(event.target.value)}
              >
                <option value="programming">Programming</option>
                <option value="design">Design</option>
                <option value="business">Business</option>
                <option value="marketing">Marketing</option>
              </Select>
            </Flex>
          </FormControl>

          <FormControl id="language" mb="4">
            <FormLabel>Language</FormLabel>
            <Select
              placeholder="Select Language"
              value={language}
              onChange={event => setLanguage(event.target.value)}
            >
              <option value="programming">Programming</option>
              <option value="design">Design</option>
              <option value="business">Business</option>
              <option value="marketing">Marketing</option>
            </Select>
          </FormControl>

          <FormControl>
            <label htmlFor="image-input">Course Image</label>
            <Input type="file" accept="image/*" onChange={handleImageUpload} />
          </FormControl>

          <FormControl>
            <label htmlFor="image-input">Course Promotional Video</label>
            <Input
              type="file"
              accept="image/*"
              onChange={handlePromotionalImageUpload}
            />
          </FormControl>

          {/* <FormControl id="description" mb="4">
            <FormLabel>Description</FormLabel>
            <Textarea
              placeholder="Enter a course description"
              value={description}
              onChange={event => setDescription(event.target.value)}
            />
          </FormControl> */}

          {/* <FormControl id="price" mb="4">
            <FormLabel>Price</FormLabel>
            <NumberInput value={price} onChange={value => setPrice(value)}>
              <NumberInputField placeholder="Enter the course price" />
              <NumberInputStepper>
                <NumberIncrementStepper />
                <NumberDecrementStepper />
              </NumberInputStepper>
            </NumberInput>
          </FormControl> */}

          <FormControl mt="20px">
          <label htmlFor="image-input">Instructor Profile</label>
          <div>
          <Link href="#" isExternal color="blue.500" fontWeight="bold" fontSize="md">
            + Edit your profile
          </Link>
          </div>
          </FormControl>


          <Center mt="100px" gap="20px">
            <Button
              w="15%"
              colorScheme="blue"
              _hover={{ bg: 'white', color: 'blue' }}
            >
              Previous
            </Button>
            <Button
              w="15%"
              colorScheme="blue"
              _hover={{ bg: 'white', color: 'blue' }}
            >
              Next
            </Button>
          </Center>
        </form>
      </Box>
    </Flex>
</Box>


  );
};

export default CoursePostingForm;
